body.script:
      //Copyright (c) 2022-2023 Creadores Program ©2023

      //Do not Modify these Variants!
      var send = {
        Info: '[INFO] ',
        Warning: '[WARN] ',
        Err: '[ERROR] ',
        Log: ' '
      }


      //Config The Game:
      var properties = {
        apiKey: false, //Please don't move this! (Not implemented)
         allowPrompt: true, //Ask if you want to exit the Game
        confMessageExit: "Seguro que Quieres Salir del Juego?", //Message before exiting the Game
        areYouReallySure: false, //Don't Modify This!
        errorGameMessage: 'Hubo un error o bug en el Juego! Error 5001! Contacta al Soporte de Creadores Program en: https://discord.gg/mrmHcwxXff Error: ', //In-Game Error Message
        soundMenu: new Audio ('https://github.com/Trollhunters501/CreadorCraft-Arcade/raw/main/music/menu-arcade.ogg') //Game Menu Audio
        }

      //Starting Executable Code JS and Error Detection:
      try {
      console.info(send.Info + 'Please wait...');
      console.info(send.Info + 'Powered by Creadores Program ©2023');
      console.info(send.Info + 'Loading Game...');

        //Code to confirm the exit of the Game:
function areYouSure() {
    if(properties.allowPrompt){
        if (!properties.areYouReallySure && true) {
            properties.areYouReallySure = true;

            return properties.confMessageExit;
        }
    }else{
        properties.allowPrompt = true;
    }
}

window.onbeforeunload = areYouSure;
        //End of the Code to Confirm the exit of the Game.

        //Game preload code JS!
        $(window).load(function() {
    $(".loader").fadeOut("slow");
});
        //End of preload code JS.

        //Full Screen API Code!
        function launchFullScreen(element) {
  if(element.requestFullScreen) {
    element.requestFullScreen();
  } else if(element.mozRequestFullScreen) {
    element.mozRequestFullScreen();
  } else if(element.webkitRequestFullScreen) {
    element.webkitRequestFullScreen();
  }
}
//Launch in full screen in browsers that support it
 function cancelFullScreen() {
     if(document.cancelFullScreen) {
         document.cancelFullScreen();
     } else if(document.mozCancelFullScreen) {
         document.mozCancelFullScreen();
     } else if(document.webkitCancelFullScreen) {
         document.webkitCancelFullScreen();
     }
 }
        //Full Screen API End Code.

        //Game Audio Code!
        playBtn.addEventListener('click', ()=> {
          properties.soundMenu.loop = true;
          properties.soundMenu.play();});

        stopBtn.addEventListener('click', ()=> {
          properties.soundMenu.pause();});
        //End of Game Audio Code.
        } catch (error) {

alert (properties.errorGameMessage + error);
          console.error(send.Err + error);
//End of Executable Code JS and Error Detection.
}
